					["semver"] = "1.0.6",
					["Temp_Initialization"] = "function(script)\n    -- OPTIONS\n    -- relative size { 0.8, 0.6 }\n    -- absolute size { 200, 30, true }\n    local bars = {\n        inCombat = { 1.05, 1.15 },\n        outCombat = { 1.05, 1.15 },\n    }\n    local auras = { 1.0,1.0 }\n    \n    --------------------\n    -- DO NOT EDIT BELOW\n    --------------------\n    script.auras = {\n        options = auras,\n        custom = nil,\n        default = { Plater.db.profile.aura_width, Plater.db.profile.aura_height },\n        defaultTextSize = Plater.db.profile.aura_timer_text_size,\n        shouldResize = false,\n    }\n    script.bars = {\n        options = bars,\n        custom = nil,\n        default = nil,\n        shouldResizeInCombat = false,\n        shouldResizeOutCombat = false,\n    }\n    script.isInitialized = false\n    \n    local function isValidOptions(size)\n        return size ~= nil and (type(size) == \"table\")\n    end\n    \n    local function getCustomSize(default, options)\n        if isValidOptions(options) then\n            local defaultWidth, defaultHeight = unpack(default)\n            local width, height, exact = unpack(options)\n            \n            if exact then\n                width = math.floor(width or defaultWidth)\n                height = math.floor(height or defaultHeight)\n            else\n                width = (width and math.floor(defaultWidth * width)) or defaultWidth\n                height = (height and math.floor(defaultHeight * height)) or defaultHeight\n            end\n            \n            if width ~= defaultWidth or height ~= defaultHeight then\n                return { width, height }\n            end\n        end\n    end\n    \n    function script:Init(unitFrame)\n        if self.isInitialized then\n            return\n        end\n        \n        local config = Plater.GetConfig(unitFrame)\n        \n        self.bars.default = {\n            inCombat = config.health_incombat,\n            outCombat = config.health,\n        }\n        \n        self.bars.custom = {\n            inCombat = getCustomSize(self.bars.default.inCombat, self.bars.options.inCombat),\n            outCombat = getCustomSize(self.bars.default.outCombat, self.bars.options.outCombat)\n        }\n        \n        if self.bars.custom.inCombat then\n            self.bars.shouldResizeInCombat = true\n        end\n        if self.bars.custom.outCombat then\n            self.bars.shouldResizeOutCombat = true\n        end\n        \n        self.auras.custom = getCustomSize(self.auras.default, self.auras.options)\n        \n        if self.auras.custom then\n            self.auras.shouldResize = true\n        end\n        \n        self.isInitialized = true\n    end\n    \n    local function setCustomHealthBar(unitFrame, size)\n        unitFrame.customHealthBarWidth = (size and size[1]) or nil\n        unitFrame.customHealthBarHeight = (size and size[2]) or nil\n    end\n    \n    local function setCustomCastBar(unitFrame, size)\n        unitFrame.customCastBarWidth = (size and size[1]) or nil\n        unitFrame.customCastBarHeight = (size and size[2]) or nil\n    end\n    \n    local function resizeAuras(unitFrame)\n        local auraContainer = unitFrame.BuffFrame.PlaterBuffList\n        local width, height = unpack(script.auras.custom)\n        \n        for _,aura in ipairs(auraContainer) do\n            if aura:IsShown() then\n                aura:SetSize(width, height)\n                aura.Icon:SetSize(width-2, height-2)\n                aura.TimerText:SetTextHeight(height-3)\n            end\n        end\n    end\n    \n    function script.restoreAuras(unitFrame)\n        local auraContainer = unitFrame.BuffFrame.PlaterBuffList\n        local width, height = unpack(script.auras.default)\n        local textSize = script.auras.defaultTextSize\n        \n        for _,aura in ipairs(auraContainer) do\n            aura:SetSize(width, height)\n            aura.Icon:SetSize(width-2, height-2)\n           