						["Nameplate Updated"] = "function (self, unitId, unitFrame, envTable)\n    \n    local buffSpecialGlow = true\n    \n    \n    -- functions --\n    local auraContainers = {unitFrame.BuffFrame.PlaterBuffList}\n    \n    if (Plater.db.profile.buffs_on_aura2) then\n        auraContainers [2] = unitFrame.BuffFrame2.PlaterBuffList\n    end\n    \n    for containerID = 1, #auraContainers do\n        \n        local auraContainer = auraContainers [containerID]\n        \n        for index, auraIcon in ipairs (auraContainer) do\n            if (auraIcon:IsShown() and auraIcon.RemainingTime > 0 and (envTable.glowSpells[auraIcon.spellId] and envTable.glowSpells[auraIcon.spellId] >= auraIcon.RemainingTime))  then\n                if not auraIcon.pandemicGlowStarted then\n                    Plater.StartPixelGlow(auraIcon, nil, envTable.options)\n                    auraIcon.pandemicGlowEffect = auraIcon.pandemicGlowEffect or Plater.CreateIconGlow (auraIcon)\n                    auraIcon.pandemicGlowEffect:SetColor( envTable.AntsColor, envTable.GlowColor)\n                    auraIcon.pandemicGlowEffect:Show()\n                    auraIcon.pandemicGlowStarted = true\n                end\n            else\n                if auraIcon.pandemicGlowStarted then\n                    Plater.StopPixelGlow(auraIcon, envTable.options.key)\n                    auraIcon.pandemicGlowEffect:Hide()\n                    auraIcon.pandemicGlowStarted = false\n                end\n            end                \n        end\n        \n    end\nend",
